        isLoggedIn,
        user,
        loginWithLink,
        loginWithSocial,
        availableLogins,
        logout,
        connect,
        theme,
        logo,
    };
};
const useProvider = () => {
    const context = React.useContext(AuthContext);
    if (context == null) {
