    }
    return {
        isLoggedIn: context.isLoggedIn,
        loading: context.loading,
        provider: context.provider,
    };
};

const WAIT_TEXT = {
    SOCIAL: "Please complete the login to proceed",
    LINK: "Please complete the login by clicking on email",
};

